                            },
                            {
                                xtype: 'combobox',
                                fieldLabel: '销售公司',
                                anchor: '80%',
                                id: 'mod_slsCompanyCode',
                                name: 'slsCompanyCode',
                                store: 'customer.SlsCompanyStore',
                                valueField: 'id',
                                displayField: 'name',
                                typeAhead: true,
                                forceSelection: true,
                                allowBlank: false,
                                queryMode: 'local',
                                triggerAction: 'all',
                                emptyText: '请选择销售公司',
                                blankText: '请选择销售公司',
                                listeners: {
                                    select: function () {
                                        var slsCompanyId = this.getValue();
                                        //联动销售大区和销售片区
                                        var slsBigAreaStore = Ext.getCmp('mod_slsBigAreaCode').store;
                                        var slsSmallAreaStore = Ext.getCmp('mod_slsSmallAreaCode').store;
                                        slsBigAreaStore.load({
                                            callback: function (o, response, success) {
                                                if (success) {
                                                    if (slsBigAreaStore.getCount()) {
                                                        var slsBigAreaId = slsBigAreaStore.getAt(0).get('id');
                                                        Ext.getCmp('mod_slsBigAreaCode').setValue(slsBigAreaId);
                                                        slsSmallAreaStore.load({
                                                            callback: function (o, response, success) {
                                                                if (success) {
                                                                    if (slsSmallAreaStore.getCount()) {
                                                                        Ext.getCmp('mod_slsSmallAreaCode').setValue(slsSmallAreaStore.getAt(0).get('id'));
                                                                    }
                                                                } else {
                                                                    window.top.location.href = "/errors";
                                                                }
                                                            },
                                                            params: {
                                                                grareaId: slsCompanyId,
                                                                pareaId: slsBigAreaId
                                                            }
                                                        });
                                                    } else {
                                                        slsSmallAreaStore.removeAll();
                                                        Ext.getCmp('mod_slsSmallAreaCode').setValue("");
                                                    }
                                                } else {
                                                    window.top.location.href = "/errors";
                                                }
                                            },
                                            params: {
                                                pareaId: slsCompanyId
                                            }
                                        });
                                        //联动部门和负责人
                                        var userStore = Ext.getCmp('mod_userid').store;
                                        var deptStore = Ext.getCmp('mod_deptCode').store;
                                        deptStore.load({
                                            callback: function (o, response, success) {
                                                if (success) {
                                                    if (deptStore.getCount()) {
                                                        var deptId = deptStore.getAt(0).get('deptid');
                                                        Ext.getCmp('mod_deptCode').setValue(deptId);
                                                        userStore.load({
                                                            callback: function (o, response, success) {
                                                                if (success) {
                                                                    if (userStore.getCount()) {
                                                                        Ext.getCmp('mod_userid').setValue(userStore.getAt(0).get('userid'));
                                                                    }
                                                                } else {
                                                                    window.top.location.href = "/errors";
                                                                }
                                                            },
                                                            params: {
                                                                deptId: deptId
                                                            }
                                                        });
                                                    } else {
                                                        userStore.removeAll();
                                                        Ext.getCmp('mod_userid').setValue("");
                                                    }
                                                } else {
                                                    window.top.location.href = "/errors";
                                                }
                                            },
                                            params: {
                                                companyId: slsCompanyId
                                            }
                                        });
                                    }
                                }
                            },
