                                listeners: {
                                    select: function () {
                                        var provinceId = this.getValue();
                                        var cityStore = Ext.getCmp('mod_cityCode').store;
                                        var districtStore = Ext.getCmp('mod_districtCode').store;
                                        cityStore.load({
                                            callback: function (o, response, success) {
                                                if (success) {
                                                    if (cityStore.getCount()) {
                                                        var cityId = cityStore.getAt(0).get('id');
                                                        Ext.getCmp('mod_cityCode').setValue(cityId);
                                                        districtStore.load({
                                                            callback: function (o, response, success) {
                                                                if (success) {
