                                                                        Ext.getCmp('mod_custAddr').setValue(Ext.getCmp('mod_provinceCode').getRawValue() + Ext.getCmp('mod_cityCode').getRawValue() + Ext.getCmp('mod_districtCode').getRawValue() + Ext.getCmp('mod_street').getValue());
                                                                    }
                                                                } else {
                                                                    window.top.location.href = "/errors";
                                                                }
                                                            },
